import * as actionTypes from './actionTypes'
import axios from 'axios'

export const authStart = () => {
    return {
        type : actionTypes.AUTH_START
    }
}

export const authSuccess = (authData) => {
    return {
        type : actionTypes.AUTH_SUCCESS,
        authData : authData
    }
}


export const authFail = (error) => {
    return {
        type : actionTypes.AUTH_FAIL,
        error : error
    }
}

export const auth = (emails, passwords) => {
    console.log
    return dispatch => {
       const authData = {
           email : emails,
           password : passwords,
           returnSecureToken : true
       }
        dispatch(authStart())
        axios.post('https://www.googleapis.com/identitytoolkit/v3/relyingparty/verifyCustomToken?key=AIzaSyDilfJaHLQTbICQgEmX2kIs_ErkUeecSCY', authData)
             .then(res => {
                console.log(res)
                dispatch(authSuccess(res))
             })
             .catch(err => {
                 console.log(err)
                 dispatch(authFail(err))
             })
    }
}
