import React from 'react'
import styles from './Modal.css'
import { Transform } from 'stream';


const Modal = (props) =>(
    <div 
    className={styles.Modal}
    style = {
        Transform = props.show ? 'translateY(0)' : 'translateY(-100vh)',
        opacity = props.show ? '1' : ''
    }>
        {props.children}
    </div>
)

export default Modal
