import React, {Component} from 'react'
import Button from '../../../Components/UI/Button/Button'
import styles from './ContactData.css'
import axios from '../../../axios-orders'
import Spinner from '../../../Components/UI/Spinner/Spinner'
import Input from '../../../Components/UI/Input/Input'

import {Redirect} from 'react-router-dom'




class ContactData extends Component {

    state = {
        orderForm :{

                name : {
                    elementType:'input',
                    elementConfig:{
                        type:'text',
                        placeholder:'Your Name'
                    },
                    value : '',
                    validation : {
                        required : true
                    },
                    valid : false
                },
                street : {
                    elementType:'input',
                    elementConfig:{
                        type:'text',
                        placeholder:'Street'
                    },
                    value : '',
                    validation : {
                        required : true
                    },
                    valid : false
                
                },
                zipCode : {
                    elementType:'input',
                    elementConfig:{
                        type:'text',
                        placeholder:'ZIP Code'
                    },
                    value : '',
                    validation : {
                        required : true
                    },
                    valid : false
                },
                country : {
                    elementType:'input',
                    elementConfig:{
                        type:'text',
                        placeholder:'Your Country'
                    },
                    value : '',
                    validation : {
                        required : true
                    },
                    valid : false
                },
                email : {
                    elementType:'input',
                    elementConfig:{
                        type:'email',
                        placeholder:'Your Mail'
                    },
                    value : '',
                    validation : {
                        required : true
                    },
                    valid : false
                },
                deliveryMethod : {
                    elementType:'select',
                    elementConfig:{
                      options : [
                          {value:'fastest', displayValue:'Fastest'},
                          {value:'Cheapest', displayValue:'Cheapest'}
                        ]
                    },
                    value : ''
                },
        },
        loading : false,
        home : false
    }
    orderHandler =  (event) =>{
            event.preventDefault()
            const formData ={}
            for (let formElementIdentifier in this.state.orderForm){
                formData[formElementIdentifier] = this.state.orderForm[formElementIdentifier].value
            }
            this.setState({loading:true})
               const data = {
                   ingredients : this.props.ingredients,
                   price : this.props.price,
                   orderData:formData
                }

               axios.post('/orders.json', data)
               .then(response => {
                  
                  this.setState({loading:false, home:true})
                  
               })
               .catch(error => {
                this.setState({loading:false})
                
               })
    }

    onChangeHandler = (event, inputElementIdentifier)=>{
        const updatedForm = {
            ...this.state.orderForm
        }
        const updatedFormElement = {
            ...updatedForm[inputElementIdentifier]
        }
        updatedFormElement.value = event.target.value
        updatedForm[inputElementIdentifier] = updatedFormElement
        this.setState({orderForm:updatedForm})
    }


    checkValidity
    render(){
        let redirect = null
        if(this.state.home){
            redirect = <Redirect to='/' />
        }
        const formElementsArray = []
        for(let key in this.state.orderForm){
            formElementsArray.push({
                id: key,
                config : this.state.orderForm[key]
            })
        }
       
        let form = (<form onSubmit={this.orderHandler}>
                        {formElementsArray.map(formElement=>(
                            <Input 
                                key={formElement.id}
                                
                                elementtype={formElement.config.elementType}
                                elementConfig={formElement.config.elementConfig}
                                value={formElement.config.value} 
                                change={(event)=>this.onChangeHandler(event, formElement.id)}/>

                        ))}
                         
                         <Button btnType='Success' >Place Order </Button>
                    
                    </form> 
                    )
        if(this.state.loading){
            form = <Spinner />
        }

        return(
            <div className={styles.ContactData}>
                <h3>Please Enter Your Contact Details</h3>
                {form}
                {redirect}           
            </div>
        )
    }
}


export default ContactData
