import * as actionTypes from './actionTypes'
import axios from 'axios'

export const authStart = () => {
    return {
        type : actionTypes.AUTH_START
    }
}

export const authSuccess = (token, userId) => {
    return {
        type : actionTypes.AUTH_SUCCESS,
        token : token,
        userId : userId
    }
}


export const authFail = (error) => {
    return {
        type : actionTypes.AUTH_FAIL,
        error : error
    }
}

export const logout = () => {
    return {
        type : actionTypes.AUTH_LOGOUT
    }
}

export const checkAuthTimeOut = (expirationTime) => {
    return dispatch => {
        setTimeout(() => {
            dispatch(logout())
        }, expirationTime * 1000 )
    }
}

export const auth = (email, password, isSignUp) => {
   
    return dispatch => {
       const authData = {
           email : email,
           password : password,
           returnSecureToken : true
       }
       let url = 'https://www.googleapis.com/identitytoolkit/v3/relyingparty/signupNewUser?key=AIzaSyDilfJaHLQTbICQgEmX2kIs_ErkUeecSCY'
       if(!isSignUp){
           url = 'https://www.googleapis.com/identitytoolkit/v3/relyingparty/verifyPassword?key=AIzaSyDilfJaHLQTbICQgEmX2kIs_ErkUeecSCY'
       }
        dispatch(authStart())
        axios.post(url, authData)
             .then(res => {
                console.log(res)
                dispatch(authSuccess(res.data.idToken, res.data.localId))
                dispatch(checkAuthTimeOut(res.data.expiresIn))
             })
             .catch(err => {    
                 dispatch(authFail(err.response.data.error))
             })
    }
}

export const setAuthRedirectPath = (path) => {
    return {
        type : actionTypes.SET_AUTH_REDIRECT_PATH,
        
    }
}
